import kotlinx.coroutines.Deferred
import kotlinx.coroutines.async
import kotlinx.coroutines.awaitAll
import kotlinx.coroutines.coroutineScope


fun removeDuplicates(list1: MutableList<String>, list2: String): List<String> {
    val wordsToRemove = list1.filter { list2.contains(it) }
    list1.removeAll(wordsToRemove)
    return wordsToRemove
}
fun main() {
    val list1: MutableList<String> = mutableListOf("apple", "banana", "orange")
    val list2: String = "I like apple and orange"

    val removedWords = removeDuplicates(list1, list2)

    println("Removed words: $removedWords")
    println("Updated list1: $list1")


    val str = ""

    println("str length: ${str.length}")
}
